	private static List<String> getAllClass(File file) {
		List<String> ret = new ArrayList<>();
		if (file.isDirectory()) {
			File[] list = file.listFiles();
			for (File i : list) {
			}
			for (File i : list) {
				List<String> j = getAllClass(i);
				ret.addAll(j);
			}
		} else {
			ret.add(file.getAbsolutePath());
		}
		return ret;
	}

